	function setDefaultHighlight(id) {
		var link = document.getElementById(id);
		link.setAttribute("class", "quoteBtmLink highlight");
		link.setAttribute("className", "quoteBtmLink highlight"); //for IE
	}
	function changeChartBoxFrequency(freq, dur, id) {
		var i = document.getElementById("quoteChartBoxIframe");
		var container = document.getElementById("chartNav");
		var elements = container.getElementsByTagName("a");
		var link = document.getElementById(id);
		if (i) {
			i.src = "/charts/cr/?symbol=EAD.PA&display=mountain&width=172&height=124&frequency=" + freq + "&duration=" + dur;
			for(var i=0;i<elements.length;i++) {
				
				if(elements[i].getAttribute("class")=="quoteBtmLink highlight" || elements[i].getAttribute("className")=="quoteBtmLink highlight") {
					elements[i].setAttribute("class", "quoteBtmLink");
					elements[i].setAttribute("className", "quoteBtmLink"); //for IE
				}
			}
			link.setAttribute("class", "quoteBtmLink highlight");
			link.setAttribute("className", "quoteBtmLink highlight"); //for IE
		}
		return false;
	}
