    function OnClickGo(ticker) {
      try {
		var url = "/finance/stocks/overview?symbol=[ric]".replace("[ric]", ticker);
		document.location.href = url;
      }catch(e){}
    }
    function OnStockSearchSubmit() {
      try {
        var ticker = document.getElementById("stockSearchTicker").value;
        if(ticker === "" || ticker === "Enter a symbol") {
	        return false;
        } else {
        	OnClickGo(ticker);
        	return false;
        }
      }catch(e){}
    }
    function OnTextFocus(e) {
      try {
        if(this.value == "Enter a symbol") {
          this.value = "";
        }
      }catch(e){}
    }
    function OnTextBlur(e) {
      try {
        if(this.value == "") {
          this.value = "Enter a symbol";
        }
      }catch(e){}
    }
    function OnTextKeyPress(e) {
		if (!e) var e = window.event;
        if(e) {
          if(e.keyCode === 13 || e.which === 13 || e.charCode === 13) {
          	OnStockSearchSubmit();
            return false;
          }
        }     
    }


